// src/components/OOTDForm.jsx
import "../css/OOTDForm.css";
import { useState } from "react";
import { FaMapMarkerAlt, FaSearch } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import KakaoMapModal from "./KakaoMapModal";

export default function OOTDForm() {
  const navigate = useNavigate();
  const [location, setLocation] = useState("");          // 吏��룄 �삉�뒗 �룄�떆 �꽑�깮�쑝濡� ����옣
  const [selectedCity, setSelectedCity] = useState("");  // 踰꾪듉 �겢由��쑝濡� 怨좊Ⅸ �룄�떆紐�
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [styles, setStyles] = useState([]);
  const [request, setRequest] = useState("");

  const styleOptions = [
    "�뒪�듃由�", "誘몃땲硫�", "鍮덊떚吏�", "罹먯＜�뼹", "�윭釉붾━", "�삤�뵾�뒪", "�똾�뵒", "�븘硫붿뭅吏�",
  ];

  const toggleStyle = (style) => {
    setStyles((prev) =>
      prev.includes(style)
        ? prev.filter((s) => s !== style)
        : [...prev, style]
    );
  };

  const handleSubmit = () => {
    const finalLocation = location || selectedCity;
    if (finalLocation && styles.length > 0 && request.trim() !== "") {
      navigate("/result", { state: { location: finalLocation, styles, request } });
    } else {
      alert("紐⑤뱺 �빆紐⑹쓣 �엯�젰�빐二쇱꽭�슂.");
    }
  };

  return (
    <div className={`ootd-container ${isModalOpen ? "blurred" : ""}`}>
      <h1 className="title">OOTD-AI</h1>
      <p className="subtitle">AI媛� 異붿쿇�븯�뒗 �삤�뒛�쓽 �셿踰쏀븳 猷�</p>

      <div className="form-card">
        {/* �쐞移� �꽑�깮 */}
        <div className="form-section">
          <label className="form-label">�쐞移� �꽑�깮</label>
          <div className="location-box" onClick={() => setIsModalOpen(true)}>
            <FaMapMarkerAlt className="icon" />
            <span className="location-label">
              {location || selectedCity || "�쐞移� �꽑�깮"}
            </span>
          </div>
        </div>

        {/* �뒪����씪 �꽑�깮 */}
        <div className="form-section">
          <label className="form-label">�뒪����씪 (以묐났 �꽑�깮 媛��뒫)</label>
          <div className="search-bar">
            <input placeholder="search text" />
            <FaSearch className="icon" />
          </div>
          <div className="style-grid">
            {styleOptions.map((style, idx) => (
              <button
                key={idx}
                className={`style-btn ${styles.includes(style) ? "selected" : ""}`}
                onClick={() => toggleStyle(style)}
              >
                {style}
              </button>
            ))}
          </div>
        </div>

        {/* �슂泥��궗�빆 */}
        <div className="form-section">
          <label className="form-label">異붽�� �슂泥��궗�빆</label>
          <textarea
            placeholder="�썝�븯�뒗 �뒪����씪�씠�굹 �슂泥��궗�빆�쓣 �엯�젰�빐二쇱꽭�슂."
            value={request}
            onChange={(e) => setRequest(e.target.value)}
          />
        </div>

        {/* �젣異� 踰꾪듉 */}
        <button className="submit-btn" onClick={handleSubmit}>
          異붿쿇諛쏄린
        </button>
      </div>

      {/* 吏��룄 紐⑤떖 */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="modal-title">�쐞移� �꽑�깮</h3>
            <input
              type="text"
              placeholder="�윍� �쐞移� 寃��깋"
              className="modal-search"
            />
            <div className="modal-map">
              <KakaoMapModal onSelect={(addr) => setLocation(addr)} />
            </div>
            <div className="city-grid">
              {["�꽌�슱", "�씤泥�", "遺��궛", "���援�", "愿묒＜", "����쟾"].map((city) => (
                <button
                  key={city}
                  className={`city-btn ${selectedCity === city ? "active" : ""}`}
                  onClick={() => {
                    setSelectedCity(city);
                    setLocation(city);
                  }}
                >
                  {city}
                </button>
              ))}
            </div>
            <button className="modal-confirm" onClick={() => setIsModalOpen(false)}>
              �솗�씤
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
