import React, { useEffect, useState } from "react";
import { FaSearch } from "react-icons/fa";
import "../css/KakaoMapModal.css";

const loadKakaoSDK = () => {
  return new Promise((resolve) => {
    if (window.kakao && window.kakao.maps) {
      resolve(window.kakao);
    } else {
      const script = document.createElement("script");
      script.src =
        "https://dapi.kakao.com/v2/maps/sdk.js?appkey=2380b0c6e08137415b69715b13439001&autoload=false&libraries=services";
      script.async = true;
      document.head.appendChild(script);
      script.onload = () => resolve(window.kakao);
    }
  });
};

export default function KakaoMapModal({ onSelect }) {
  const [mapInstance, setMapInstance] = useState(null);
  const [marker, setMarker] = useState(null);
  const [geocoder, setGeocoder] = useState(null);
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    loadKakaoSDK().then((kakao) => {
      kakao.maps.load(() => {
        const container = document.getElementById("kakao-map");
        if (!container) return;

        let defaultLatLng = new kakao.maps.LatLng(37.5665, 126.9780); // 서울 기본값

        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              defaultLatLng = new kakao.maps.LatLng(
                position.coords.latitude,
                position.coords.longitude
              );
              initMap(defaultLatLng);
            },
            () => initMap(defaultLatLng)
          );
        } else {
          initMap(defaultLatLng);
        }

        function initMap(centerLatLng) {
          const map = new kakao.maps.Map(container, { center: centerLatLng, level: 3 });
          const newMarker = new kakao.maps.Marker({ position: centerLatLng, map });
          const geocoderService = new kakao.maps.services.Geocoder();

          setMapInstance(map);
          setMarker(newMarker);
          setGeocoder(geocoderService);

          kakao.maps.event.addListener(map, "click", (mouseEvent) => {
            const latlng = mouseEvent.latLng;
            newMarker.setPosition(latlng);

            geocoderService.coord2Address(latlng.getLng(), latlng.getLat(), (result, status) => {
              if (status === kakao.maps.services.Status.OK) {
                onSelect({
                  address: result[0].address.address_name,
                  lat: latlng.getLat(),
                  lng: latlng.getLng(),
                });
              }
            });
          });
        }
      });
    });
  }, [onSelect]);

  const handleSearch = () => {
    if (!searchInput.trim() || !geocoder || !mapInstance || !marker) return;

    geocoder.addressSearch(searchInput, (result, status) => {
      if (status === window.kakao.maps.services.Status.OK) {
        const moveLatLng = new window.kakao.maps.LatLng(result[0].y, result[0].x);
        mapInstance.setCenter(moveLatLng);
        marker.setPosition(moveLatLng);
        onSelect({
          address: result[0].address_name,
          lat: parseFloat(result[0].y),
          lng: parseFloat(result[0].x),
        });
      }
    });
  };

  return (
    <div>
      {/* ✅ 검색창 + 돋보기 버튼 */}
      <div className="search-wrapper">
        <input
          type="text"
          placeholder="원하는 위치 검색"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
          className="modal-search"
        />
        <button className="search-btn" onClick={handleSearch}>
          <FaSearch />
        </button>
      </div>

      {/* ✅ 지도 */}
      <div id="kakao-map" className="kakao-map"></div>
    </div>
  );
}
