import React, { useEffect } from "react";

export default function KakaoMapModal({ onSelect }) {
  useEffect(() => {
    // �̹� SDK �ε�Ǿ����� Ȯ��
    if (!window.kakao) {
      const script = document.createElement("script");
      script.src =
        "https://dapi.kakao.com/v2/maps/sdk.js?appkey=2380b0c6e08137415b69715b13439001&autoload=false&libraries=services";
      script.async = true;
      document.head.appendChild(script);

      script.onload = () => {
        initMap();
      };
    } else {
      initMap();
    }

    function initMap() {
      window.kakao.maps.load(() => {
        const container = document.getElementById("kakao-map");
        if (!container) return;

        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.9780), // ����
          level: 3,
        };

        const map = new window.kakao.maps.Map(container, options);
        const geocoder = new window.kakao.maps.services.Geocoder();
        const marker = new window.kakao.maps.Marker({ map });

        // ���� Ŭ�� �̺�Ʈ
        window.kakao.maps.event.addListener(map, "click", (mouseEvent) => {
          const latlng = mouseEvent.latLng;
          marker.setPosition(latlng);

          // ��ǥ �� �ּ� ��ȯ
          geocoder.coord2Address(
            latlng.getLng(),
            latlng.getLat(),
            (result, status) => {
              if (status === window.kakao.maps.services.Status.OK) {
                const address = result[0].address.address_name;
                onSelect(address);
              }
            }
          );
        });
      });
    }
  }, [onSelect]);

  return (
    <div
      id="kakao-map"
      style={{ width: "100%", height: "300px", background: "#eee" }}
    ></div>
  );
}
