import React, { useEffect, useState } from "react"; // ✅ useState, useEffect 추가
import "../css/KakaoMapModal.css";

export default function KakaoMapModal({ onSelect, onClose }) {
  const [mapInstance, setMapInstance] = useState(null);
  const [marker, setMarker] = useState(null);
  const [searchInput, setSearchInput] = useState("");

  // ✅ Kakao SDK 로드 & 지도 초기화는 그대로 유지

  const handleSearch = (callback) => {
    if (!searchInput.trim()) {
      alert("검색어를 입력하세요.");
      return;
    }

    const ps = new window.kakao.maps.services.Places();
    ps.keywordSearch(searchInput, (data, status) => {
      if (status === window.kakao.maps.services.Status.OK) {
        const firstResult = data[0];
        const lat = parseFloat(firstResult.y);
        const lng = parseFloat(firstResult.x);

        if (mapInstance && marker) {
          const moveLatLng = new window.kakao.maps.LatLng(lat, lng);
          mapInstance.setCenter(moveLatLng);
          marker.setPosition(moveLatLng);

          // ✅ 선택한 위치 부모로 전달
          onSelect({
            address: firstResult.address_name || firstResult.place_name,
            lat: lat,
            lng: lng,
          });

          if (callback) callback(); // ✅ 모달 닫기 콜백 실행
        }
      } else {
        alert("검색 결과가 없습니다.");
      }
    });
  };

  return (
    <div>
      <div className="search-container">
        <input
          type="text"
          placeholder="원하는 위치 검색"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
          className="modal-search"
        />
      </div>

      <div id="kakao-map" className="kakao-map"></div>

      <div className="city-grid">
        {/* 주요 도시 버튼 유지 */}
      </div>

      {/* ✅ 확인 버튼에서 handleSearch 실행 + 모달 닫기 */}
      <button
        className="modal-confirm"
        onClick={() => handleSearch(onClose)}
      >
        확인
      </button>
    </div>
  );
}
