import React, { useEffect } from "react";
import "../css/KakaoMapModal.css";

const KakaoMapModal = ({ isOpen, onClose }) => {
  useEffect(() => {
    if (isOpen) {
      const container = document.getElementById("kakao-map");
      const options = {
        center: new window.kakao.maps.LatLng(37.5665, 126.9780), // ���� ��û ��ǥ
        level: 3, // Ȯ�� ����
      };
      const map = new window.kakao.maps.Map(container, options);

      // ��Ŀ �߰� (�ɼ�)
      const marker = new window.kakao.maps.Marker({
        position: new window.kakao.maps.LatLng(37.5665, 126.9780),
      });
      marker.setMap(map);
    }
  }, [isOpen]);

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>�ݱ�</button>
        <div id="kakao-map" style={{ width: "500px", height: "400px" }}></div>
      </div>
    </div>
  );
};

export default KakaoMapModal;
