// src/components/KakaoMapModal.jsx
import { useEffect, useRef } from "react";

export default function KakaoMapModal({ onSelect }) {
  const mapRef = useRef(null);

  useEffect(() => {
    const { kakao } = window;

    kakao.maps.load(() => {
      const map = new kakao.maps.Map(mapRef.current, {
        center: new kakao.maps.LatLng(37.5665, 126.9780),
        level: 6,
      });

      const geocoder = new kakao.maps.services.Geocoder();
      const marker = new kakao.maps.Marker({ map });

      kakao.maps.event.addListener(map, "click", function (mouseEvent) {
        const latlng = mouseEvent.latLng;
        marker.setPosition(latlng);

        geocoder.coord2Address(latlng.getLng(), latlng.getLat(), function (result, status) {
          if (status === kakao.maps.services.Status.OK) {
            const address = result[0].address.address_name;
            onSelect(address); // �θ� ������Ʈ�� ����
          }
        });
      });
    });
  }, [onSelect]);

  return <div ref={mapRef} style={{ width: "100%", height: "300px", borderRadius: "12px" }} />;
}
