import "../css/OOTDForm.css";
import { useState } from "react";
import { FaMapMarkerAlt, FaSearch } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import KakaoMap from "./KakaoMapModal";

export default function OOTDForm() {
  const navigate = useNavigate();
  const [location, setLocation] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [styles, setStyles] = useState([]);
  const [request, setRequest] = useState("");

  const styleOptions = [
    "Ïä§Ìä∏Î¶ø",
    "ÎØ∏ÎãàÎ©Ä",
    "ÎπàÌã∞ÏßÄ",
    "Ï∫êÏ£ºÏñº",
    "Îü¨Î∏îÎ¶¨",
    "Ïò§ÌîºÏä§",
    "ÎåÑÎîî",
    "ÏïÑÎ©îÏπ¥ÏßÄ",
  ];

  const toggleStyle = (style) => {
    setStyles((prev) =>
      prev.includes(style) ? prev.filter((s) => s !== style) : [...prev, style]
    );
  };

  const handleSubmit = () => {
    const finalLocation = location || selectedCity;

    if (finalLocation && styles.length > 0 && request.trim() !== "") {
      navigate("/result", {
        state: { location: finalLocation, styles, request },
      });
    } else {
      alert("Î™®Îì† Ìï≠Î™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
    }
  };

  return (
    <div className="ootd-container">
      <h1 className="title">OOTD-AI</h1>
      <p className="subtitle">AIÍ∞Ä Ï∂îÏ≤úÌïòÎäî Ïò§ÎäòÏùò ÏôÑÎ≤ΩÌïú Î£©</p>

      <div className="form-card">
        <div className="form-section">
          <label className="form-label">ÏúÑÏπò ÏÑ†ÌÉù</label>
          <div className="location-box" onClick={() => setIsModalOpen(true)}>
            <FaMapMarkerAlt className="icon" />
            <span className="location-label">
              {location || selectedCity || "ÏúÑÏπò ÏÑ†ÌÉù"}
            </span>
          </div>
        </div>

        <div className="form-section">
          <label className="form-label">Ïä§ÌÉÄÏùº (Ï§ëÎ≥µ ÏÑ†ÌÉù Í∞ÄÎä•)</label>
          <div className="search-bar">
            <input placeholder="search text" />
            <FaSearch className="icon" />
          </div>
          <div className="style-grid">
            {styleOptions.map((style, idx) => (
              <button
                key={idx}
                className={`style-btn ${styles.includes(style) ? "selected" : ""}`}
                onClick={() => toggleStyle(style)}
              >
                {style}
              </button>
            ))}
          </div>
        </div>

        <div className="form-section">
          <label className="form-label">Ï∂îÍ∞Ä ÏöîÏ≤≠ÏÇ¨Ìï≠</label>
          <textarea
            placeholder="ÏõêÌïòÎäî Ïä§ÌÉÄÏùºÏù¥ÎÇò ÏöîÏ≤≠ÏÇ¨Ìï≠ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî."
            value={request}
            onChange={(e) => setRequest(e.target.value)}
          />
        </div>

        <button className="submit-btn" onClick={handleSubmit}>
          Ï∂îÏ≤úÎ∞õÍ∏∞
        </button>
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h3 className="modal-title">ÏúÑÏπò ÏÑ†ÌÉù</h3>
            <input
              type="text"
              placeholder="üîç ÏúÑÏπò Í≤ÄÏÉâ"
              className="modal-search"
            />
            <div className="modal-map">
              <KakaoMap />
            </div>
            <div className="city-grid">
              {["ÏÑúÏö∏", "Ïù∏Ï≤ú", "Î∂ÄÏÇ∞", "ÎåÄÍµ¨", "Í¥ëÏ£º", "ÎåÄÏ†Ñ"].map((city) => (
                <button
                  key={city}
                  className={`city-btn ${selectedCity === city ? "active" : ""}`}
                  onClick={() => setSelectedCity(city)}
                >
                  {city}
                </button>
              ))}
            </div>
            <button
              className="modal-confirm"
              onClick={() => {
                setLocation(selectedCity);
                setIsModalOpen(false);
              }}
            >
              ÌôïÏù∏
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
